mapper.insert:
INSERT INTO rst (rst_no, rst_name, star, catag_no, loc, loc_dtl, tel, opn_tm, brck_tm, dnnr_tm, lo_tm) VALUES ( #{rst_no}, #{rst_name}, #{star}, #{catag_no},
#{loc}, #{loc_dtl}, #{tel}, #{opn_tm}, #{brck_tm}, #{dnnr_tm}, #{lo_tm}
) ON DUPLICATE KEY UPDATE rst_no = #{rst_no}, rst_name = #{rst_name}, star = #{star}, catag_no = #{catag_no},
loc = #{loc}, loc_dtl = #{loc_dtl}, tel = #{tel}, opn_tm = #{opn_tm}, brck_tm = #{brck_tm}, dnnr_tm = #{dnnr_tm}, lo_tm = #{lo_tm}
